import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertEventSchema, insertRegistrationSchema, insertLeaderboardSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', async (req: any, res) => {
    try {
      if (!req.isAuthenticated() || !req.user?.claims?.sub) {
        return res.status(401).json({ message: "Unauthorized" });
      }
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Event routes
  app.get('/api/events', async (req, res) => {
    try {
      const { status, category, search, organizerId, startDate, endDate } = req.query;

      const filters: any = {};
      if (status) filters.status = status as string;
      if (category) filters.category = category as string;
      if (search) filters.search = search as string;
      if (organizerId) filters.organizerId = organizerId as string;
      if (startDate) filters.startDate = new Date(startDate as string);
      if (endDate) filters.endDate = new Date(endDate as string);

      const events = await storage.getEvents(filters);
      res.json(events);
    } catch (error) {
      console.error("Error fetching events:", error);
      res.status(500).json({ message: "Failed to fetch events" });
    }
  });

  app.get('/api/events/:id', async (req, res) => {
    try {
      const { id } = req.params;
      const event = await storage.getEventWithDetails(id);

      if (!event) {
        return res.status(404).json({ message: "Event not found" });
      }

      res.json(event);
    } catch (error) {
      console.error("Error fetching event:", error);
      res.status(500).json({ message: "Failed to fetch event" });
    }
  });

  app.post('/api/events', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user || (user.role !== 'admin' && user.role !== 'organizer')) {
        return res.status(403).json({ message: "Insufficient permissions" });
      }

      const eventData = insertEventSchema.parse({
        ...req.body,
        organizerId: userId,
        resources: Array.isArray(req.body.resources) 
          ? req.body.resources as { title: string; url: string; type: string }[]
          : [],
        tags: Array.isArray(req.body.tags) ? req.body.tags as string[] : [],
      });

      const event = await storage.createEvent(eventData);
      res.status(201).json(event);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid event data", errors: error.errors });
      }
      console.error("Error creating event:", error);
      res.status(500).json({ message: "Failed to create event" });
    }
  });

  app.patch('/api/events/:id', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      const event = await storage.getEvent(id);
      if (!event) {
        return res.status(404).json({ message: "Event not found" });
      }

      if (!user || (user.role !== 'admin' && event.organizerId !== userId)) {
        return res.status(403).json({ message: "Insufficient permissions" });
      }

      const updateData = insertEventSchema.partial().parse({
        ...req.body,
        resources: Array.isArray(req.body.resources) 
          ? req.body.resources as { title: string; url: string; type: string }[]
          : undefined,
        tags: Array.isArray(req.body.tags) ? req.body.tags as string[] : undefined,
      });
      const updatedEvent = await storage.updateEvent(id, updateData);
      res.json(updatedEvent);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid event data", errors: error.errors });
      }
      console.error("Error updating event:", error);
      res.status(500).json({ message: "Failed to update event" });
    }
  });

  app.delete('/api/events/:id', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      const event = await storage.getEvent(id);
      if (!event) {
        return res.status(404).json({ message: "Event not found" });
      }

      if (!user || (user.role !== 'admin' && event.organizerId !== userId)) {
        return res.status(403).json({ message: "Insufficient permissions" });
      }

      await storage.deleteEvent(id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting event:", error);
      res.status(500).json({ message: "Failed to delete event" });
    }
  });

  // Registration routes
  app.get('/api/events/:eventId/registrations', isAuthenticated, async (req: any, res) => {
    try {
      const { eventId } = req.params;
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      const event = await storage.getEvent(eventId);
      if (!event) {
        return res.status(404).json({ message: "Event not found" });
      }

      if (!user || (user.role !== 'admin' && event.organizerId !== userId)) {
        return res.status(403).json({ message: "Insufficient permissions" });
      }

      const registrations = await storage.getEventRegistrations(eventId);
      res.json(registrations);
    } catch (error) {
      console.error("Error fetching registrations:", error);
      res.status(500).json({ message: "Failed to fetch registrations" });
    }
  });

  app.get('/api/users/:userId/registrations', isAuthenticated, async (req: any, res) => {
    try {
      const { userId } = req.params;
      const currentUserId = req.user.claims.sub;
      const user = await storage.getUser(currentUserId);

      if (!user || (user.role !== 'admin' && currentUserId !== userId)) {
        return res.status(403).json({ message: "Insufficient permissions" });
      }

      const registrations = await storage.getUserRegistrations(userId);
      res.json(registrations);
    } catch (error) {
      console.error("Error fetching user registrations:", error);
      res.status(500).json({ message: "Failed to fetch user registrations" });
    }
  });

  app.post('/api/events/:eventId/register', isAuthenticated, async (req: any, res) => {
    try {
      const { eventId } = req.params;
      const userId = req.user.claims.sub;

      const event = await storage.getEvent(eventId);
      if (!event) {
        return res.status(404).json({ message: "Event not found" });
      }

      if (event.status !== 'upcoming') {
        return res.status(400).json({ message: "Registration is not open for this event" });
      }

      if (new Date() > event.registrationDeadline) {
        return res.status(400).json({ message: "Registration deadline has passed" });
      }

      if (event.maxParticipants && event.currentParticipants && event.currentParticipants >= event.maxParticipants) {
        return res.status(400).json({ message: "Event is full" });
      }

      const existingRegistration = await storage.getRegistration(eventId, userId);
      if (existingRegistration) {
        return res.status(400).json({ message: "Already registered for this event" });
      }

      const registrationData = insertRegistrationSchema.parse({
        eventId,
        userId,
        teamName: req.body.teamName || null,
        teamMembers: Array.isArray(req.body.teamMembers) 
          ? req.body.teamMembers as { name: string; email: string; role?: string }[]
          : [],
      });

      const registration = await storage.createRegistration(registrationData);
      res.status(201).json(registration);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid registration data", errors: error.errors });
      }
      console.error("Error creating registration:", error);
      res.status(500).json({ message: "Failed to register for event" });
    }
  });

  app.delete('/api/registrations/:id', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      // Get registration to check ownership
      const registrations = await storage.getUserRegistrations(userId);
      const registration = registrations.find(r => r.id === id);

      if (!registration && user?.role !== 'admin') {
        return res.status(404).json({ message: "Registration not found" });
      }

      await storage.deleteRegistration(id);
      res.status(204).send();
    } catch (error) {
      console.error("Error canceling registration:", error);
      res.status(500).json({ message: "Failed to cancel registration" });
    }
  });

  // Leaderboard routes
  app.get('/api/events/:eventId/leaderboard', async (req, res) => {
    try {
      const { eventId } = req.params;
      const leaderboard = await storage.getEventLeaderboard(eventId);
      res.json(leaderboard);
    } catch (error) {
      console.error("Error fetching leaderboard:", error);
      res.status(500).json({ message: "Failed to fetch leaderboard" });
    }
  });

  app.post('/api/events/:eventId/leaderboard', isAuthenticated, async (req: any, res) => {
    try {
      const { eventId } = req.params;
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      const event = await storage.getEvent(eventId);
      if (!event) {
        return res.status(404).json({ message: "Event not found" });
      }

      if (!user || (user.role !== 'admin' && event.organizerId !== userId)) {
        return res.status(403).json({ message: "Insufficient permissions" });
      }

      const entryData = insertLeaderboardSchema.parse({
        eventId,
        ...req.body,
      });

      const entry = await storage.createLeaderboardEntry(entryData);
      res.status(201).json(entry);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid leaderboard data", errors: error.errors });
      }
      console.error("Error creating leaderboard entry:", error);
      res.status(500).json({ message: "Failed to create leaderboard entry" });
    }
  });

  // Stats route
  app.get('/api/stats', async (req, res) => {
    try {
      const stats = await storage.getStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching stats:", error);
      res.status(500).json({ message: "Failed to fetch stats" });
    }
  });

  // Admin routes
  app.patch('/api/users/:userId/role', isAuthenticated, async (req: any, res) => {
    try {
      const { userId } = req.params;
      const { role } = req.body;
      const currentUserId = req.user.claims.sub;
      const currentUser = await storage.getUser(currentUserId);

      if (!currentUser || currentUser.role !== 'admin') {
        return res.status(403).json({ message: "Insufficient permissions" });
      }

      if (!['admin', 'organizer', 'participant'].includes(role)) {
        return res.status(400).json({ message: "Invalid role" });
      }

      const updatedUser = await storage.upsertUser({ id: userId, role });
      res.json(updatedUser);
    } catch (error) {
      console.error("Error updating user role:", error);
      res.status(500).json({ message: "Failed to update user role" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  events,
  eventRegistrations,
  leaderboard,
  type User,
  type UpsertUser,
  type Event,
  type InsertEvent,
  type EventRegistration,
  type InsertEventRegistration,
  type LeaderboardEntry,
  type InsertLeaderboardEntry,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, asc, and, or, ilike, gte, lte, count, sql } from "drizzle-orm";

// Interface for storage operations
export interface IStorage {
  // User operations (IMPORTANT) these user operations are mandatory for Replit Auth.
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Event operations
  getEvents(filters?: {
    status?: string;
    category?: string;
    search?: string;
    organizerId?: string;
    startDate?: Date;
    endDate?: Date;
  }): Promise<Event[]>;
  getEvent(id: string): Promise<Event | undefined>;
  getEventWithDetails(id: string): Promise<(Event & { organizer: User; registrations: EventRegistration[] }) | undefined>;
  createEvent(event: InsertEvent): Promise<Event>;
  updateEvent(id: string, event: Partial<InsertEvent>): Promise<Event>;
  deleteEvent(id: string): Promise<void>;

  // Registration operations
  getEventRegistrations(eventId: string): Promise<(EventRegistration & { user: User })[]>;
  getUserRegistrations(userId: string): Promise<(EventRegistration & { event: Event })[]>;
  getRegistration(eventId: string, userId: string): Promise<EventRegistration | undefined>;
  createRegistration(registration: InsertEventRegistration): Promise<EventRegistration>;
  updateRegistration(id: string, registration: Partial<InsertEventRegistration>): Promise<EventRegistration>;
  deleteRegistration(id: string): Promise<void>;

  // Leaderboard operations
  getEventLeaderboard(eventId: string): Promise<(LeaderboardEntry & { registration: EventRegistration & { user: User } })[]>;
  createLeaderboardEntry(entry: InsertLeaderboardEntry): Promise<LeaderboardEntry>;
  updateLeaderboardEntry(id: string, entry: Partial<InsertLeaderboardEntry>): Promise<LeaderboardEntry>;

  // Stats operations
  getStats(): Promise<{
    totalEvents: number;
    totalParticipants: number;
    activeEvents: number;
    completedEvents: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations (IMPORTANT) these user operations are mandatory for Replit Auth.
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Event operations
  async getEvents(filters?: {
    status?: string;
    category?: string;
    search?: string;
    organizerId?: string;
    startDate?: Date;
    endDate?: Date;
  }): Promise<Event[]> {
    let query = db.select().from(events);

    const conditions = [];

    if (filters?.status) {
      conditions.push(eq(events.status, filters.status as any));
    }

    if (filters?.category) {
      conditions.push(eq(events.category, filters.category));
    }

    if (filters?.search) {
      conditions.push(
        or(
          ilike(events.title, `%${filters.search}%`),
          ilike(events.description, `%${filters.search}%`)
        )
      );
    }

    if (filters?.organizerId) {
      conditions.push(eq(events.organizerId, filters.organizerId));
    }

    if (filters?.startDate) {
      conditions.push(gte(events.startDate, filters.startDate));
    }

    if (filters?.endDate) {
      conditions.push(lte(events.endDate, filters.endDate));
    }

    if (conditions.length > 0) {
      query = query.where(and(...conditions));
    }

    return await query.orderBy(desc(events.createdAt));
  }

  async getEvent(id: string): Promise<Event | undefined> {
    const [event] = await db.select().from(events).where(eq(events.id, id));
    return event;
  }

  async getEventWithDetails(id: string): Promise<(Event & { organizer: User; registrations: EventRegistration[] }) | undefined> {
    const [eventResult] = await db
      .select()
      .from(events)
      .leftJoin(users, eq(events.organizerId, users.id))
      .where(eq(events.id, id));

    if (!eventResult || !eventResult.events) return undefined;

    const registrations = await db
      .select()
      .from(eventRegistrations)
      .where(eq(eventRegistrations.eventId, id));

    return {
      ...eventResult.events,
      organizer: eventResult.users!,
      registrations,
    };
  }

  async createEvent(event: InsertEvent): Promise<Event> {
    const [newEvent] = await db.insert(events).values(event).returning();
    return newEvent;
  }

  async updateEvent(id: string, event: Partial<InsertEvent>): Promise<Event> {
    const [updatedEvent] = await db
      .update(events)
      .set({ ...event, updatedAt: new Date() })
      .where(eq(events.id, id))
      .returning();
    return updatedEvent;
  }

  async deleteEvent(id: string): Promise<void> {
    await db.delete(events).where(eq(events.id, id));
  }

  // Registration operations
  async getEventRegistrations(eventId: string): Promise<(EventRegistration & { user: User })[]> {
    const registrations = await db
      .select()
      .from(eventRegistrations)
      .leftJoin(users, eq(eventRegistrations.userId, users.id))
      .where(eq(eventRegistrations.eventId, eventId));

    return registrations.map(reg => ({
      ...reg.event_registrations!,
      user: reg.users!,
    }));
  }

  async getUserRegistrations(userId: string): Promise<(EventRegistration & { event: Event })[]> {
    const registrations = await db
      .select()
      .from(eventRegistrations)
      .leftJoin(events, eq(eventRegistrations.eventId, events.id))
      .where(eq(eventRegistrations.userId, userId));

    return registrations.map(reg => ({
      ...reg.event_registrations!,
      event: reg.events!,
    }));
  }

  async getRegistration(eventId: string, userId: string): Promise<EventRegistration | undefined> {
    const [registration] = await db
      .select()
      .from(eventRegistrations)
      .where(
        and(
          eq(eventRegistrations.eventId, eventId),
          eq(eventRegistrations.userId, userId)
        )
      );
    return registration;
  }

  async createRegistration(registration: InsertEventRegistration): Promise<EventRegistration> {
    const [newRegistration] = await db
      .insert(eventRegistrations)
      .values(registration)
      .returning();

    // Update event participant count
    await db
      .update(events)
      .set({ 
        currentParticipants: sql`${events.currentParticipants} + 1` 
      })
      .where(eq(events.id, registration.eventId));

    return newRegistration;
  }

  async updateRegistration(id: string, registration: Partial<InsertEventRegistration>): Promise<EventRegistration> {
    const [updatedRegistration] = await db
      .update(eventRegistrations)
      .set(registration)
      .where(eq(eventRegistrations.id, id))
      .returning();
    return updatedRegistration;
  }

  async deleteRegistration(id: string): Promise<void> {
    const [registration] = await db
      .select()
      .from(eventRegistrations)
      .where(eq(eventRegistrations.id, id));

    await db.delete(eventRegistrations).where(eq(eventRegistrations.id, id));

    if (registration) {
      // Update event participant count
      await db
        .update(events)
        .set({ 
          currentParticipants: sql`${events.currentParticipants} - 1` 
        })
        .where(eq(events.id, registration.eventId));
    }
  }

  // Leaderboard operations
  async getEventLeaderboard(eventId: string): Promise<(LeaderboardEntry & { registration: EventRegistration & { user: User } })[]> {
    const leaderboardEntries = await db
      .select()
      .from(leaderboard)
      .leftJoin(eventRegistrations, eq(leaderboard.registrationId, eventRegistrations.id))
      .leftJoin(users, eq(eventRegistrations.userId, users.id))
      .where(eq(leaderboard.eventId, eventId))
      .orderBy(asc(leaderboard.position));

    return leaderboardEntries.map(entry => ({
      ...entry.leaderboard!,
      registration: {
        ...entry.event_registrations!,
        user: entry.users!,
      },
    }));
  }

  async createLeaderboardEntry(entry: InsertLeaderboardEntry): Promise<LeaderboardEntry> {
    const [newEntry] = await db.insert(leaderboard).values(entry).returning();
    return newEntry;
  }

  async updateLeaderboardEntry(id: string, entry: Partial<InsertLeaderboardEntry>): Promise<LeaderboardEntry> {
    const [updatedEntry] = await db
      .update(leaderboard)
      .set(entry)
      .where(eq(leaderboard.id, id))
      .returning();
    return updatedEntry;
  }

  // Stats operations
  async getStats(): Promise<{
    totalEvents: number;
    totalParticipants: number;
    activeEvents: number;
    completedEvents: number;
  }> {
    const [totalEventsResult] = await db.select({ count: count() }).from(events);
    const [totalParticipantsResult] = await db.select({ count: count() }).from(eventRegistrations);
    const [activeEventsResult] = await db
      .select({ count: count() })
      .from(events)
      .where(eq(events.status, "live"));
    const [completedEventsResult] = await db
      .select({ count: count() })
      .from(events)
      .where(eq(events.status, "completed"));

    return {
      totalEvents: totalEventsResult.count,
      totalParticipants: totalParticipantsResult.count,
      activeEvents: activeEventsResult.count,
      completedEvents: completedEventsResult.count,
    };
  }
}

export const storage = new DatabaseStorage();

